import{aF as o,e,f as a,V as t}from"./chunks/framework.54wzUQT-.js";const f=JSON.parse('{"title":"ShowDoc简介","description":"","frontmatter":{},"headers":[],"relativePath":"docs/fe/showdoc.md","filePath":"docs/fe/showdoc.md","lastUpdated":1716277301000}'),c={name:"docs/fe/showdoc.md"},h=t('<h1 id="showdoc简介" tabindex="-1">ShowDoc简介 <a class="header-anchor" href="#showdoc简介" aria-label="Permalink to &quot;ShowDoc简介&quot;">​</a></h1><h2 id="showdoc是什么" tabindex="-1">ShowDoc是什么 <a class="header-anchor" href="#showdoc是什么" aria-label="Permalink to &quot;ShowDoc是什么&quot;">​</a></h2><p>每当接手一个他人开发好的模块或者项目，看着那些没有写注释的代码，我们都无比难受。如果有相关的文档不仅仅可以提高效率，也可以减少方向性的错误。Show me the doc ！！</p><p>程序员都很希望别人能写技术文档，而自己却很不希望要写文档。因为写文档需要花大量的时间去处理格式排版，想着新建的word文档放在哪个目录等各种非技术细节。</p><p>word文档零零散散地放在团队不同人那里，需要文档的人基本很难有整个系统的文档，艰难获取文档文档本身也是效率不高的一种体现。</p><p>ShowDoc就是一个非常适合IT团队的在线文档分享工具，它可以加快团队之间沟通的效率。</p><h2 id="showdoc的本地装部署" tabindex="-1"><a href="https://www.showdoc.com.cn/help/65610" target="_blank" rel="noreferrer">showdoc的本地装部署</a> <a class="header-anchor" href="#showdoc的本地装部署" aria-label="Permalink to &quot;[showdoc的本地装部署](https://www.showdoc.com.cn/help/65610)&quot;">​</a></h2>',7),s=[h];function d(r,n,w,_,l,i){return e(),a("div",null,s)}const m=o(c,[["render",d]]);export{f as __pageData,m as default};
